INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (379, 3, 'Advance Deposit', 'advance-deposit', 'Payment made by a Guest(Cash or Credit Card) to secure a Room Reservation.', '');
