INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (391, 3, 'Airline Classes of Service', 'airline-classes-of-service', 'variety of terms used to express a particular type of aircraft cabin service. Classes vary with types of compartments, seating comfort, and amenities, with variation between domestic and international flights, and denoted by a fare code on the ticket.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (392, 3, 'Airline Reporting Conference (ARC)', 'airline-reporting-conference-arc', 'a consortium of airline companies, who by agreement, provide a method of approving authorized agency locations for the sale of transportation and cost-effective procedures for processing records and funds to carriers. Not all airlines are ARC companies.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (393, 3, 'All Inclusive', 'all-inclusive', 'A hotel plan that includes all services, Room, Food, Beverage etc. for one set price. Usually offers shopping etc. and are usually “Destination/Resort” Hotels', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (394, 3, 'All Inclusive', 'all-inclusive', 'A form of package holiday where the majority of services offered at the destination are included in the price paid prior to departure (e.g. refreshments, excursions, amenities, gratuities, etc).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (395, 3, 'Allocentric', 'allocentric', 'Of a minority of tourists: adventurous, outgoing, self-confident, independent, needing little tourist infrastructure. Enjoys high contact with locals.', '');
