INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (398, 3, 'All Suite', 'all-suite', 'A level of service provided by a hotel, for a guest who will desire an “at home” atmosphere- .i.e. bedroom, Bathroom, Kitchenette, Living room (usually equipped with sofa-bed)', '');
