INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (450, 3, 'Bed &amp; Breakfast (B&amp;B)', 'bed-amp-breakfast-b-amp-b', 'Lodging and Breakfast offered in a home-setting. (Common in Europe and now gaining popularity in Singapore)', '');
