INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (455, 3, 'Bell Staff', 'bell-staff', 'people who lift and tote baggage, familiarize guests with their new surround-ings, run errands, deliver supplies, provide guests with information on in-house marketing efforts and local attractions, and act as the hospitality link between the lodging estab-lishment and the guest', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (456, 3, 'Benchmarking', 'benchmarking', 'Measuring performance against that of best in class companies, determining how the best-in-class achieve those performance levels and using this information as a basis for your own company\'s targets, strategies and implementation (Pryor, 1989)', '');
