INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (460, 3, 'bill-to-account', 'bill-to-account', 'An extension of credit to a guest by an individual hotel, which requires the guest or the guest’s employer to establish a line of credit and to adhere to a regular payment schedule', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (461, 3, 'Biodiversity', 'biodiversity', 'A variety of wildlife in an area', '');
