INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (465, 3, 'Block', 'block', 'a number of rooms, seats, or space reserved in advance, usually by wholesalers, tour operators, or receptive operators who intend to sell them as components of tour packages.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (466, 3, 'Blocked Rooms', 'blocked-rooms', 'Specific rooms or suites pre-assigned to a guest prior to his or her arrival. Block rooms may not be reassigned to another reservation or walking-in guest.', '');
