INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (470, 3, 'Bonding', 'bonding', 'The guarantee of protection for a supplier or consumer. In the travel industry, certain bonding programs are mandatory. The ARC insists that travel agents be bonded to protect the airlines against defaults. Professional operators and agents buy bonds voluntarily to protect their clients.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (471, 3, 'Booking Form', 'booking-form', 'A document which tour purchasers must complete which gives the operator full particulars about who is buying the tour. It states exactly what is being purchased, ( including options) and must be signed as acknowledgment that the liability clause has been read and understood.', '');
