INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (502, 3, 'Cash Bank', 'cash-bank', 'A specific amount of paper money and coins issued to a cashier to be used for making change.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (503, 3, 'Cash Deposit', 'cash-deposit', 'A deposit made by a guest while checking into a hotel as a payment for all or part of the amounts expected to be due the hotel. Different from an &quot;advance deposit,&quot; which see.', '');
