INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (505, 3, 'Cashier', 'cashier', 'A person who processes guest check- outs, collects payments (Cash or Credit card) and makes change for guest. Gives daily Cash control Report.', '');
