INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (509, 3, 'Certified Tour Professional', 'certified-tour-professional', 'CTP : a designation conferred upon tour professionals who have completed a prescribed course of academic study, professional service, tour employment and evaluation requirements. It is administered by the National Tour Association.', '');
