INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (564, 3, 'Complimentary Room', 'complimentary-room', 'A guest room for which no charge is made. Complimentary rooms with a tour group are usually occupied by the tour manager or driver.', '');
