INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (566, 3, 'Computer Reservation Systems (CRS)', 'computer-reservation-systems-crs', 'Computerised Reservation Systems used for inventory management by airlines, hotels and other facilities. CRSs can allow direct access through terminals for intermediaries to check availability, make reservations and print tickets.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (567, 3, 'Concession', 'concession', 'A hotel or business service provided by an independent Tenant (Concessionaire)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (568, 3, 'Concessionaire', 'concessionaire', 'A firm which, under contract rights, operates for another party (in many cases, a government agency) food and beverage services, lodging facilities, and other services on-site at an attraction.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (569, 3, 'Concierge', 'concierge', 'A hotel employee who handles restaurant and tour reservations, travel arrangements, and other details for hotel guests.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (570, 3, 'Concierge', 'concierge', 'A person who provides an endless array of information on entertainment, sports, amusements, transportation, tours, church services, and baby-sitting in a particular city or town.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (571, 3, 'Conditions', 'conditions', 'The section or clause of a transportation/tour contract which specifies what is not included and which may spell out the circumstances under which the contract many be invalidated.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (572, 3, 'Condominium (Condo)', 'condominium-condo', 'A multi-unit facility, where each owner shares responsibility for Public space. A Maintenance fee is usually paid and each owner may rent or lease his unit.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (573, 3, 'Condominium / Condo Hotels', 'condominium-condo-hotels', 'Hotels similar to timeshare hotels. The difference between the two lies in the type of ownership. Units in condominium hotels have only one owner instead of multiple owners, each for a limited amount of time each year.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (574, 3, 'Conference Call', 'conference-call', 'A conversation in which three or more persons are linked by telephone.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (575, 3, 'Confidential Tariff', 'confidential-tariff', 'A schedule of wholesale rates distributed in confidence to travel wholesalers and agents. Better known as a net rate.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (576, 3, 'Configuration', 'configuration', 'The interior arrangement of a vehicle, particularly an airplane. The same airplane, for example, may be configured for 190 coach-class passengers, or it may hold 12 first-class passengers and 170 coach passengers, Configuration is also used in conjunction with how the plane is arranged such as three seats on each side or in larger planes two seats on each side with four middle seats.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (577, 3, 'Confirmation Number', 'confirmation-number', 'A number given to a reservation maker used to verify that a reservation has been made.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (578, 3, 'Confirmed Reservation', 'confirmed-reservation', 'A hotel reservation with a commitment to honor it.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (579, 3, 'Confirmed Reservation', 'confirmed-reservation', 'An oral or written agreement by a supplier that he has received and will honor a reservation. Oral confirmations have no legal weight. Even written or telegraphed confirmations have specified or implied limitations. e.g.: a hotel not honoring a reservation after 6 pm., unless late arrival has been guaranteed in some manner.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (580, 3, 'Confirmed Reservations', 'confirmed-reservations', 'Prospective guests who have a reservation for accommodations that is honored until a specified time.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (581, 3, 'Connecting Room', 'connecting-room', 'A room with a private door permitting access from one room to the other without use of hotel corridor. Connecting room is often confused with adjoining room, which does not have a connecting door.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (582, 3, 'Conservation', 'conservation', 'The protection and maintenance of nature while allowing for its ecologically sustainable use.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (583, 3, 'Consolidation', 'consolidation', 'Cancellation by a charter tour operator of one or more tours/flights associated with a specific charter departure or departure period, with the transfer of passengers to another charter tour/flight to depart on or near the same day.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (584, 3, 'Consortium', 'consortium', 'A group of independent travel agencies that are together under one name to pool their customer services, marketing, knowledge, etc.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (585, 3, 'Consortium', 'consortium', 'A loosely knit group of independently owned and managed companies such as travel agencies, tour operators, hotels, or other suppliers, with a joint marketing distribution process.', '');
