INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (628, 3, 'Culture', 'culture', 'A set of shared norms and values which establish a sense of identity for those who share them. Typically applied at the level of nation and/or race.', '');
