INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (636, 3, 'Customs', 'customs', 'The common term for a government agency charged with collecting duty on specified items imported into that country. The agency also restricts the entry of persons and forbidden items without legal travel documents.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (637, 3, 'Cut-off Date', 'cut-off-date', 'Designated day when the buyer must release or add commitments to their event or tour', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (638, 3, 'Cut-off', 'cut-off', 'The time at which reservations are stopped for a group or designated segment (eliminating that rate category).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (639, 3, 'Cycle of Service', 'cycle-of-service', 'The progression, through a hotel\'s department, of a guest\'s request for products and services.', '');
