INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (641, 3, 'Daily Announcement Board', 'daily-announcement-board', 'An inside listing of the daily activities of the hotel (time, group, and room assignment)', '');
