INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (650, 3, 'Day Visitors', 'day-visitors', 'Visitors who arrive and leave the same day, irrespective of why they are travelling.', '');
