INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (657, 3, 'Degradation', 'degradation', 'Any decline in the quality of natural or cultural resources, or the viability of ecosystems, that is caused directly or indirectly by humans.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (658, 3, 'Deluxe Tour', 'deluxe-tour', 'In travel usage, presumably of the highest standard.', '');
