INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (663, 3, 'Departure Tax', 'departure-tax', 'Fee collected from the traveler by the host country at the time of departure.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (664, 3, 'Departure', 'departure', 'A guest\'s settling of his or her account and checking out of the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (665, 3, 'Dependency Theory', 'dependency-theory', 'This theory maintains that developing countries are kept in a position of dependency and underdevelopment due to existing economic and institutional power structures sustained by leading Western nations. Dependency theorists argue that the policies and activities of multinational corporations, national bilateral and multinational aid agencies such as the World Bank and the International Monetary Fund (IMF) tend to widen the gap between rich and poor countries and perpetuate the dependency of developing nations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (666, 3, 'Deposit Policy', 'deposit-policy', 'A specified amount or a percentage of the total bill due on a specified date prior to arrival.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (667, 3, 'Deposit', 'deposit', 'An advance payment required to obtain confirmed space.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (668, 3, 'Deregulation', 'deregulation', 'The act of removing regulations from the travel industry. The Airline Deregulation Act of 1978, which amended the Federal Aviation Act of 1958, provided for the end of the Civil Aeronautics Board s regulating authority over domestic airlines on January 1, 1985, for removing travel agent exclusivity, thus paving the way for carriers to appoint and pay commissions to non-travel agents, and for the removal of antitrust immunity for travel agents. The motor-coach industry was deregulated in 1982.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (669, 3, 'Designation', 'designation', 'The act of conferring a legal status on a building which requires compliance with specific legislation on conservation and preservation.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (670, 3, 'Desk Clerk', 'desk-clerk', 'The person who verifies guest reservations, registers guests, assigns rooms, distributes keys, communicates with the housekeeping staff, answers telephones, gives information about and directions to local attractions, accepts cash and gives change, and acts as liaison between the lodging establishment and the guest as well as the community.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (671, 3, 'Destination Management Company (DMC)', 'destination-management-company-dmc', 'A company working in a specific destination to handle all bookings and arrangements for tours or conferences, including hotel accommodation, transfers, sightseeing, meetings and special events. Tour operators or conference planners are likely to use the services of a DMC because of their specialist local knowledge.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (672, 3, 'Destination Marketing Organization (DMO)', 'destination-marketing-organization-dmo', 'A category of membership of the National Tour Association which includes state or provincial tourism offices, convention and visitors bureaus, and chambers of commerce which promote a city, region, or state as a travel destination.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (673, 3, 'Destination', 'destination', 'The end point of a journey.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (674, 3, 'Destination', 'destination', 'The place to which a traveler is going. In the travel industry, any city, area, or country which can be marketed as a single entity for tourists.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (675, 3, 'Dine-around Plan', 'dine-around-plan', 'A plan that permits tourists to dine at a variety of restaurants using vouchers and coupons on a tour.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (676, 3, 'Direct Billing', 'direct-billing', 'A special arrangement, Whereby the hotel agrees to bill for charges after the guest\'s departure rather than requiring the account to be settled at the time of check out.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (677, 3, 'Direct Expense', 'direct-expense', 'The category of each department\'s operating expenses that are directly allocable to that department.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (678, 3, 'Direct Spending', 'direct-spending', 'Money that goes directly from a tourist into the economy of the destination.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (679, 3, 'Direct-mail Letters', 'direct-mail-letters', 'Letters sent directly to individuals in a targeted market group in a marketing effort.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (680, 3, 'Director of Marketing and Sales', 'director-of-marketing-and-sales', 'The person who analyzes available markets, suggests products and services to meet the needs of those markets, and sells these products and services at a profit.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (681, 3, 'Director of Security', 'director-of-security', 'The person who works with department directors to develop procedures that help ensure employee honesty and guest safety.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (682, 3, 'Tour Director', 'tour-director', 'A person, usually employed or subcontracted by the tour operator, who accompanies a tour from departure to return, acting as a guide and trouble-shooter and performing all functions to make the tour operate. Also see tour manager or escort.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (683, 3, 'Discount Rate', 'discount-rate', 'A percentage of the total sale that is charged by the credit card agency to the commercial enterprise for the convenience of accepting credit cards.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (684, 3, 'Discount', 'discount', 'A rate charged that is lower than the base rate.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (685, 3, 'Discrepancy Report', 'discrepancy-report', 'A Report of variance from normal rates and/or policies, for example, report may show a vacant room as occupied.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (686, 3, 'Discretionary Income', 'discretionary-income', 'Money received from employment or other sources which can be freely spent on leisure pursuits (such as travel and tourism) after general living costs, taxation etc. are taken into consideration.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (687, 3, 'Discrimination', 'discrimination', 'Unequal treatment of persons on grounds which are not justifiable in law, e.g. in the UK, discrimination on the grounds of sex or race.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (688, 3, 'Dishonored Reservation', 'dishonored-reservation', 'A guaranteed reservation not honored by the hotel, usually because of an oversell situation. In such cases, the guest in normally sent (&quot;walked&quot;) to another, nearby, hotel and the room paid for by the hotel with which the original reservation was made.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (689, 3, 'Disk Drive', 'disk-drive', 'A place in the computer where data is stored or read; hard or floppy—31⁄2-inch versus 51⁄4-inch.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (690, 3, 'Displacement', 'displacement', 'Stated in revenue terms as the difference between the bas rate offered for a room and the rate at which the room is actually sold. Revenue displacement occurs when lower rated business is accepted in the place of higher rated business at a time when market demand appears to be strong.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (691, 3, 'Distance Learning', 'distance-learning', 'Learning that takes place via satellite broadcasts, PictureTel, or on-line computer interaction.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (692, 3, 'Distribution', 'distribution', 'The process employed to provide customers access to the product. For travel products distribution focuses largely on the ways in which the customer can reserve or purchase the product.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (693, 3, 'Disturbance', 'disturbance', 'Accelerated change caused by human activity or extreme natural events.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (694, 3, 'Diversification', 'diversification', 'The process of developing new products for new markets, in order to achieve business growth.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (695, 3, 'Diversity', 'diversity', 'Variety; multiplicity; range; assortment.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (696, 3, 'DNCO', 'dnco', 'Did not check out.', 'dun-co');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (697, 3, 'DND', 'dnd', 'Do Not Disturb', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (698, 3, 'DND Card', 'dnd-card', 'A “Do Not Disturb” sign/card which is hung outside the room to inform hotel staff or visitors, that the occupant does not wish to be disturbed. (Some Hotels show a lit, electronic sign.)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (699, 3, 'DNS (Did Not Stay)', 'dns-did-not-stay', 'Guest left shortly after Registering.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (700, 3, 'DNS', 'dns', 'Did Not Stay', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (701, 3, 'Documentation', 'documentation', 'Printed or on-screen (monitor) instructions for operating hardware or software that accompany a specific PMS.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (702, 3, 'Domestic Escorted Tour', 'domestic-escorted-tour', 'A packaged, pre-planned itinerary, including the services of a tour manager (escort) within a travelers own country', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (703, 3, 'Domestic Independent Tour - DIT', 'domestic-independent-tour-dit', 'A custom-made tour of a part of the USA planned exclusively for a client by a travel agent.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (704, 3, 'Domestic supply of tourism commodities', 'domestic-supply-of-tourism-commodities', 'Domestic supply of tourism commodities is defined as the total production in Canada of the tourism commodities which are mainly produced by tourism industries. Not all of domestic supply is purchased by visitors, so that supply exceeds tourism demand for the national tourism indicators (NTI). For example, visitors purchase only a small proportion of food and beverage services, with most going to local consumption. Also, supply does not include imports. For example the sale of a ticket on a non-Canadian airline is excluded from supply.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (705, 3, 'Domestic Tourism', 'domestic-tourism', 'Travel within the country of residence.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (706, 3, 'Dominant Scenic Alteration', 'dominant-scenic-alteration', 'An alteration in the scenic landscape that is visually obtrusive.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (707, 3, 'Dot-matrix', 'dot-matrix', 'A printer that produces small dots printed with an inked ribbon on paper.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (708, 3, 'Double Locked (DL)', 'double-locked-dl', 'An occupied room in which the deadbolt has been turned, to prohibit/prevent entry. Only a *“Grandmaster” key or an Emergency key can open it.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (709, 3, 'Double Occupancy / Double Room', 'double-occupancy-double-room', 'Two guests to a room.( Large sized bed or 2 separate beds) Bed can be King-sized, usually offered as a “Superior/King Double Room.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (710, 3, 'Double Occupancy Percentage', 'double-occupancy-percentage', 'A measure of a hotel’s staff ability to attract more than one guest to a room.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (711, 3, 'Double Rate', 'double-rate', 'A guest-rate which includes an additional person charge.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (712, 3, 'Double', 'double', 'A guest room occupied by two people.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (713, 3, 'Double-double', 'double-double', 'A room with two double-sized (full) beds.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (714, 3, 'Double-Lock (E-key)', 'double-lock-e-key', 'To use of a special key to lock a guest room door so that neither a regular or master key can open it.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (715, 3, 'Double-occupancy Rate', 'double-occupancy-rate', 'The price per person for a room to be shared with another person; abbreviated ppdo and most often quoted in the industry.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (716, 3, 'Double-queen', 'double-queen', 'Lodging-industry jargon for a hotel room type with two queen-sized beds.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (717, 3, 'Double-room Rate', 'double-room-rate', 'The full price of a room for two people (twice the double-occupancy rate).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (718, 3, 'Down and out (slang)', 'down-and-out-slang', 'A guest room that is on the first floor and has a door that opens to the parking lot, as opposed to an interior hallway.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (719, 3, 'Down', 'down', 'A term used to describe how many rooms the hotel is overbooked, as in, &quot;We are twelve rooms down.&quot;', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (720, 3, 'Down Grade', 'down-grade', 'Change of room to lower rate or class.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (721, 3, 'Downgrade', 'downgrade', 'To move to a lesser level of accommodations or a lower class of service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (722, 3, 'Draft-style', 'draft-style', 'A good type of dot-matrix print.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (723, 3, 'Due diligence', 'due-diligence', 'Taking what is considered in law to be reasonable care.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (724, 3, 'Due Out', 'due-out', 'A guest or room expected to check-out today.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (725, 3, 'Duvet', 'duvet', 'Quilts filled with down feather or synthetic fiber. Many hotels use duvets with a decorative cover, in lieu(instead) of both blanket and bedspread. (Referred to as “comforters”).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (726, 3, 'Dwell time', 'dwell-time', 'Length of time a visitor spends at an attraction or destination. Dwell time is often taken into consideration when setting admission fees as a way of ensuring perceived value for money.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (727, 3, 'Early Arrival', 'early-arrival', 'The condition of a guest arriving before the arrival date scheduled on his or her reservation or arrival before the designated check-in time.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (728, 3, 'Early Departure', 'early-departure', 'The condition of a guest departure before the departure date scheduled on his or her reservation or departure before the designated check-out time.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (729, 3, 'Earth Check indicators', 'earth-check-indicators', 'Proprietary system belonging to Green Globe 21, which uses carefully selected indicators to measure and benchmark key environmental and social impacts, as well as operational efficiency.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (730, 3, 'Ecologically Sustainable', 'ecologically-sustainable', 'Using, conserving and enhancing the community’s resources so that ecological development is maintained, and the total quality of life can be sustained now and in the future.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (731, 3, 'eCommerce', 'ecommerce', 'Internet facilitated commerce, using electronic means for promoting, selling, distributing, and servicing products.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (732, 3, 'Economy Fares or Services', 'economy-fares-or-services', 'In U.S. domestic airline operations, passenger carriage at a level below coach service; in international operations, carriage at a level below first class.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (733, 3, 'Ecosystem', 'ecosystem', 'A dynamic system of plant, animal, fungal and micro-organism communities, and the associated non-living physical and chemical factors.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (734, 3, 'Ecotour', 'ecotour', 'A tour designed to focus on preserving the environment of environmentally sensitive areas.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (735, 3, 'Eco-tourism', 'eco-tourism', 'A combination of tourism and the environment (e.g. planning before development; sustainability of resources; economic viability of a tourism product; no negative impact on either the environment or local communities; responsibility for the environment from developers, the tourism industry and tourists; environmentally-friendly practices by all parties concerned and economic benefits flowing to local communities)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (736, 3, 'Ecotourism', 'ecotourism', 'Defined by The International Ecotourism Society as ‘responsible travel to natural areas that conserves the environment and sustains the well-being of local people’.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (737, 3, 'Ecotourists', 'ecotourists', 'Tourists who plan their vacations as a means of learning and understanding, the culture and environment of a particular area. Characteristically leaving no “foot-prints”-no damage to the environment. Many visitors to China are “Eco-Tourists”.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (738, 3, 'EDC', 'edc', 'Electronic data capture.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (739, 3, 'EDT', 'edt', 'Electronic data transfer.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (740, 3, 'Educational Tour', 'educational-tour', 'Tour designed around an educational activity, such as studying art.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (741, 3, 'Electronic Data Transfer (EDT)', 'electronic-data-transfer-edt', 'The process of electronically transferring credit-card and check data electronically over telephone lines between the merchant and financial institutions and/or credit-card companies. Also called &quot;electronic data capture.&quot;', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (742, 3, 'Electronic Key', 'electronic-key', 'A plastic key with electronic codes embedded on a magnetic strip.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (743, 3, 'Electronic Key System', 'electronic-key-system', 'A system composed of battery-powered or, less frequently, hard-wired locks; a host computer and terminals; a keypuncher; and special entry cards that are used as keys.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (744, 3, 'Elevator Operator', 'elevator-operator', 'A person who manually operates the mechanical controls of the elevator.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (745, 3, 'E-mail', 'e-mail', 'A communication system that uses an electronic network to send messages via computers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (746, 3, 'Employee Discount', 'employee-discount', 'A special rate for employees of the company or franchise-affiliated hotels.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (747, 3, 'Employee Handbook', 'employee-handbook', 'Publication that provides general guidelines concerning employee conduct.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (748, 3, 'Employment Development Department', 'employment-development-department', 'Governmental department responsible for collecting payroll taxes and other activities related to employment.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (749, 3, 'Empowerment', 'empowerment', 'Management’s act of delegating certain authority and responsibility to frontline employees.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (750, 3, 'Endangered Species', 'endangered-species', 'In severe danger of becoming extinct in the near future unless immediate steps are taken to protect the species.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (751, 3, 'Energy Conservation', 'energy-conservation', 'Positive initiatives to reduce the consumption of energy to the minimum level required.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (752, 3, 'Environment', 'environment', 'The diverse community activities and cultures of a country\'s inhabitants, as well as its scarce and sensitive natural resources.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (753, 3, 'Environmental Auditing', 'environmental-auditing', 'Inspection of a tourism organisation to assess the environmental impact of its activities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (754, 3, 'Environmental Education', 'environmental-education', 'Formal and informal learning processes that are designed to raise awareness and teach new values, knowledge and skills, in order to encourage more sustainable behavior.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (755, 3, 'Environmental Impact Assessment', 'environmental-impact-assessment', 'A study undertaken to assess the effect of an action upon a specific environment or the social or cultural integrity of a community.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (756, 3, 'Environmental Impact Statement', 'environmental-impact-statement', 'The report resulting from an environmental impact assessment.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (757, 3, 'Environmental Impact', 'environmental-impact', 'The effects that a community has on the environment as a consequence of its activities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (758, 3, 'Environmental Management Systems', 'environmental-management-systems', 'Systems established by tourism organizations with the aim of mitigating negative environmental impacts.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (759, 3, 'Environmental Scanning', 'environmental-scanning', 'The process of collecting information to carry out a systematic analysis of the forces effecting the organisation and identifying potential threats and opportunities with view to generating future strategies.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (760, 3, 'EP', 'ep', 'European Plan', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (761, 3, 'Ergonomics', 'ergonomics', 'The study of how people relate psychologically and physiologically to machines.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (762, 3, 'Errors and Omissions Insurance', 'errors-and-omissions-insurance', 'Insurance coverage equivalent to malpractice insurance, protecting an agent s or operator s staff if an act of negligence, an error, or an omission occurs which causes a client great hardship or expense.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (763, 3, 'Escort Service', 'escort-service', 'Having a uniformed security guard escort a hotel employee to a financial institution.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (764, 3, 'Escort', 'escort', 'A person who accompanies a tour from departure to return, as guide, troubleshooter, etc. A person who performs such functions only at destination. The terms &quot;host-escort&quot; or &quot;host&quot; are often used and are preferred to describe this service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (765, 3, 'Escrow Accounts', 'escrow-accounts', 'Funds placed in the custody of licensed financial institutions for safekeeping. Many contracts in travel require that agents and tour operators maintain customers deposits and prepayments in escrow accounts until the time of service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (766, 3, 'Ethnic Tour', 'ethnic-tour', 'Tour designed for people usually of the same heritage traveling to their native origin, or to a destination with ethnic relevance.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (767, 3, 'Euro', 'euro', 'The accepted currency for some European states: Belgium, Germany, Spain, France, Ireland, Italy, Luxembourg, the Netherlands, Austria, Portugal, Finland, and Greece.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (768, 3, 'European Plan - EP', 'european-plan-ep', 'A hotel rate including only the lodging accommodations - no meals are included.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (769, 3, 'Evolutionary Theories', 'evolutionary-theories', 'Theories of tourism which see destinations evolving, in the sense that the types of tourists change, or evolve, over time.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (770, 3, 'Excursion', 'excursion', 'Journey where the traveler returns to the original point of departure.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (771, 3, 'Executive Coach', 'executive-coach', 'A luxury motor-coach with seating of 25 or fewer with upscale amenities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (772, 3, 'Executive Housekeeper', 'executive-housekeeper', 'A person who is responsible for the upkeep of the guest rooms and public areas of the lodging property as well as control of guest room inventory items.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (773, 3, 'Exempt Employee', 'exempt-employee', 'An employee who is exempt from the overtime provisions of law.', '');
