INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (821, 3, 'Franchisee', 'franchisee', 'A hotel owner who has access to a national reservation system and receives the benefits of the corporation’s management expertise, financial backing, national advertising, and group purchasing.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (822, 3, 'Frequent Independent Traveler - FIT', 'frequent-independent-traveler-fit', 'Custom designed, pre-paid tour with many individualized arrangements. Also used as foreign independent traveler.', '');
