INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (825, 3, 'Front Office Manager', 'front-office-manager', 'Another title for guest services manager, but more specific. Front desk managers may not have responsibility for other guest service departments.', '');
