INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (828, 3, 'Frontline Employees', 'frontline-employees', 'Employees who deliver service to guests as front desk clerks, cashiers, switchboard operators, bellhops, concierge, and housekeeping employees.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (829, 3, 'Full House', 'full-house', '100 percent hotel occupancy; a hotel that has all its guest room occupied.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (830, 3, 'Full House', 'full-house', 'A hotel with all guest rooms occupied.', '');
