INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (843, 3, 'Gigabyte', 'gigabyte', '1,024 megabytes of formatted capacity.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (844, 3, 'Global Distribution System - GDS', 'global-distribution-system-gds', 'Distributor of hotel rooms to corporations such as travel agents, that buy rooms in large volume.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (845, 3, 'Globalization', 'globalization', 'Generally defined as the network of connections of organisations and peoples are across national, geographic and cultural borders and boundaries. These global networks are creating a shrinking world where local differences and national boundaries are being subsumed into global identities. Within the field of tourism, globalisation is also viewed in terms of the revolutions in telecommunications, finance and transport that are key factors currently influencing the nature and pace of growth of tourism in developing nations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (846, 3, 'Going “Green”', 'going-green', 'the responsibility to be environmentally “friendly”.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (847, 3, 'Government Rate', 'government-rate', 'The discounted rate sometimes available to some government travelers.', '');
