INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (849, 3, 'Green Globe / Green Globe 21', 'green-globe-green-globe-21', 'GREEN GLOBE 21 is the worldwide benchmarking and certification programme which facilitates sustainable travel and tourism for consumers, companies and communities. It is based on Agenda 21 and principles for Sustainable Development endorsed by 182 governments at the United Nations Rio de Janeiro Earth Summit in 1992. www.greenglobe21.com', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (850, 3, 'Greenhouse Effect', 'greenhouse-effect', 'The trapping of the sun’s thermal radiation by gases and water vapor, keeping the surface of the earth warmer than it would be otherwise.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (851, 3, 'Greenhouse Gases', 'greenhouse-gases', 'Gases that contribute to the greenhouse affect. These include carbon dioxide, methane and water vapour. One source of greenhouse gases is the burning of fossil fuels, such as oil, gas and coal.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (852, 3, 'Gross Income', 'gross-income', 'The difference between revenue and the cost of goods. (Different from &quot;gross operating income.&quot;)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (853, 3, 'Gross Operating Income', 'gross-operating-income', 'Net profit or loss after operating expenses have been deducted from operating revenues. (Different from &quot;gross income.&quot;)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (854, 3, 'Gross Operating Profit (GOP)', 'gross-operating-profit-gop', 'Profit before taxes, depreciation, management fees, insurance, debt service and capital expenditures are deducted.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (855, 3, 'Gross Receipts', 'gross-receipts', 'All proceeds, including revenue and taxes, collected by a company.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (856, 3, 'Ground Operator', 'ground-operator', 'A company or individual providing such services as hotel, sightseeing, transfers, and all other related services for groups. See receptive operator.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (857, 3, 'Ground Package', 'ground-package', 'Often expressed as a percentage (eg. 100% or 110%) of the lowest regular fare for the air travel scheduled.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (858, 3, 'Group Block', 'group-block', 'A specified number and type of rooms assigned to a particular group.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (859, 3, 'Group Deposit', 'group-deposit', 'Funds advanced to a hotel for the purpose of guaranteeing a group of room reservations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (860, 3, 'Group Guest', 'group-guest', 'A hotel customer traveling as part of a group of customers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (861, 3, 'Group Leader', 'group-leader', 'An individual, acting as liaison to a tour operator, acts as escort.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (862, 3, 'Guest History', 'guest-history', 'A record of details concerning the guests visits, such as frequency, corporate affiliation, credit rating, personal information or special needs. Important to better service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (863, 3, 'Guest Ledger', 'guest-ledger', 'Account, kept in the Front Office, where all guest charges and payments are logged.', '');
