INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (870, 3, 'Guest Services Department', 'guest-services-department', 'Usually the hotel department comprising the front desk, reservations, telephone, concierge, bell services and guest transportation departments.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (871, 3, 'Guest Services Manager (GSM)', 'guest-services-manager-gsm', 'Maintains the quality of guest services. Manages front office operations including reservations, check-in, check-out and accounting functions.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (872, 3, 'Guest Services Manager', 'guest-services-manager', 'The supervisor of a hotel\'s guest services department.', '');
