INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (876, 3, 'Guests', 'guests', 'Guest who will spend money at the hotel\'s food and beverage outlets, spa etc. That will maximize profit for the hotel.', '');
