INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (878, 3, 'Handle with Care (HWC) Guest', 'handle-with-care-hwc-guest', 'Guest who may have had some unpleasant experiences in the hotel or had some complaints, genuine or otherwise, are labeled as handle with care by the hotel, for the remainder of their stay.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (879, 3, 'Hard Key System', 'hard-key-system', 'A security devise consisting of the traditional hard key that fits into keyhole.', '');
