INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (882, 3, 'Hardware', 'hardware', 'Computer equipment used to process software, such as central processing units, keyboards, monitors, and printers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (883, 3, 'Head Tax', 'head-tax', 'Fee charged for arriving and departing passengers in some foreign countries.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (884, 3, 'Heritage Site', 'heritage-site', 'A place that capitalizes on its connection with heritage.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (885, 3, 'Heritage', 'heritage', 'A very broad expression that describes anything that has a link with some past event or person (e.g. cultural heritage refers to past customs and traditions with the unspoken implication that these are worthwhile or creditable)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (886, 3, 'Heritage', 'heritage', 'Things of value that are inherited which people want to keep. Heritage can be natural, cultural, tangible, intangible, personal or collective. Natural heritage is often conserved in places such as reserves and national parks. Cultural heritage practices are often conserved through ongoing traditions and practices.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (887, 3, 'Heritage', 'heritage', 'Today\'s perception of a pattern of events in the past.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (888, 3, 'High Conservation Value', 'high-conservation-value', 'Recognition of the great significance of a natural or cultural site.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (889, 3, 'High Season', 'high-season', 'A period of time when occupancy demand of a hotel is at its highest, and rates are usually at their highest as well.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (890, 3, 'History', 'history', 'A pattern of events in the past.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (891, 3, 'Hold Mail', 'hold-mail', 'Mail that has arrived either prior to a guest\'s arrival or following his or her departure.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (892, 3, 'Hollywood Twin Room', 'hollywood-twin-room', 'A room with two twin beds ( but a common headboard), which is meant for two people. If the need arises, the beds can be “bridged” together to make it appear as a single, “King-sized” bed.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (893, 3, 'Hospitality Suites', 'hospitality-suites', 'A bedroom with connection parlor room used for entertaining and/or serving food and beverage.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (894, 3, 'Hospitality Television (HTV)', 'hospitality-television-htv', 'A commercial hospitality educational organization based in Louisville, Kentucky, that provides satellite broadcasts to hotels, restaurants, and food service facilities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (895, 3, 'Hospitality', 'hospitality', 'The cordial and generous reception and entertainment of guests, either socially or commercially.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (896, 3, 'Hospitality', 'hospitality', 'The generous and cordial provision of services to a guest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (897, 3, 'Host', 'host', 'A representative of the tour operator, destination or other tour principal who provides escort service at the destination. The term host-escort is often used to make a distinction between this function and such representatives who provide only information or greeting services, or who assist at the destination with ground arrangements without actually accompanying the tour.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (898, 3, 'Hotel Broker', 'hotel-broker', 'A person who sells hotel room prize packages to corporations, sweepstakes promoters, game shows, and other sponsors.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (899, 3, 'Hotel Description Screen', 'hotel-description-screen', 'Information for a property or hotel chain in the GDS. All relevant information regarding location, amenities and policies are sorted and accessed in this arena. It is equivalent to a sales brochure about a property.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (900, 3, 'Hotel Package', 'hotel-package', 'A package offered by a hotel, usually at a special rate, sometimes consisting of room and breakfast only, and at other times consisting of transportation, room, meals, exercise facilities and other components.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (901, 3, 'Hotel Representative (Hotel Rep)', 'hotel-representative-hotel-rep', 'A member of the Marketing and Sales department of the hotel who actively seeks out groups.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (902, 3, 'Hotel Representative', 'hotel-representative', 'A member of the marketing and sales department of the hotel who actively seeks out group activities planners.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (903, 3, 'Hotelier', 'hotelier', 'An individual who is expert, in maintaining the overall operation of a hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (904, 3, 'House Bank', 'house-bank', 'Funds kept on hand by a merchant for the purpose of making change to customers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (905, 3, 'House Count', 'house-count', 'The number of persons registered in a hotel on a specific night.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (906, 3, 'House Limit', 'house-limit', 'A dollar amount set by the hotel that allows for a maximum amount of guest charges.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (907, 3, 'Housekeeper’s Room Report', 'housekeeper-s-room-report', 'A daily report that lists the occupancy status of each room according to the housekeeping department.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (908, 3, 'Housekeeper\'s Report', 'housekeeper-s-report', 'A report by the housekeeping department indicating the status of each room in the hotel (clean, dirty, vacant, etc.).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (909, 3, 'Housekeeping', 'housekeeping', 'Hotel Department responsible for the cleaning, maintenance and upkeep of the Hotel – Guest Rooms and Public Areas. Responsible for the largest/most important hotel inventory.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (910, 3, 'Housekeeping Room Status', 'housekeeping-room-status', 'Terminology that indicates availability of guest room such as Available, Clean or ready ( room is ready to be occupied ), Occupied ( guest or guests are already occupying a room ), Dirty or Stay over ( guest will not be checking out of a room on the current day ), On change ( guest has checked out of the room, but housekeeping staff has not released the room for occupancy), and Out of Order( the room is not available for occupancy because of a mechanical or other malfunction)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (911, 3, 'Housing Bureau', 'housing-bureau', 'An organization, often government sponsored, which acts as a clearing house for accommodations, particularly for conventions and other large meetings.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (912, 3, 'HRD', 'hrd', 'Human Resources Department', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (913, 3, 'HRM', 'hrm', 'Human Resource Management, concerned with the strategic management of human resources to achieve a competitive advantage.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (914, 3, 'Hub and spoke Tours', 'hub-and-spoke-tours', 'Tours which utilize a central destination with side trips of varying length to nearby destinations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (915, 3, 'Hubbart Formula', 'hubbart-formula', 'A method used to compute room rates that considers such factors as operating expenses, desired return on investment, and income from various departments in the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (916, 3, 'Human Resources Manager', 'human-resources-manager', 'The person responsible for administering federal, state, and local employment laws as well as advertising, screening, interviewing, selecting, orienting, training, and evaluating employees.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (917, 3, 'Human-made Attraction', 'human-made-attraction', 'An attraction created by people.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (918, 3, 'HWC', 'hwc', 'Handle With Care', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (919, 3, 'IATA Number', 'iata-number', 'A number used by the International Association of Travel Agents to designate each member. This is done to avoid individuals and organizations fraudulently representing themselves as travel agents.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (920, 3, 'IATA', 'iata', 'International Association of Travel Agents', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (921, 3, 'Incentive Program', 'incentive-program', 'An organized effort by management to understand employees‟ motivational concerns and develop opportunities for employees to achieve their goals and the goals of the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (922, 3, 'Immigration', 'immigration', 'The process by which a government official verifies a person’s passport, visa or origin of citizenship.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (923, 3, 'Impacts', 'impacts', 'Effects, which may be either positive or negative, felt as a result of tourism-associated activity. Tourists have at least three kinds of impacts on a destination: economic, sociocultural and environmental. Tourism also has effects on tourists, in terms of possible attitude and behavior changes.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (924, 3, 'Impromptu Travel', 'impromptu-travel', 'No arrangements booked in country of destination prior to travel. (Another name for FIT travel).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (925, 3, 'Inbound Tour Operator', 'inbound-tour-operator', 'Company specializing in domestic tours for foreign visitors in the strictest sense. Can also be used interchangeably with receptive operator.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (926, 3, 'Inbound Tour', 'inbound-tour', 'Group of travelers whose trip originated in another city or country.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (927, 3, 'Incentive Program', 'incentive-program', 'An organized effort by management to understand employees’ motivational concerns and develop opportunities for employees to achieve both their goals and the goals of the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (928, 3, 'Incentive Tour', 'incentive-tour', '(1) a trip offered as a prize, particularly to stimulate the productivity of employees or sales agents, or (2) the business of operating such travel programs', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (929, 3, 'Incentive Travel', 'incentive-travel', 'A trip offered as a prize, particularly to stimulate the productivity of employees or sales agents.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (930, 3, 'Incidental Charges', 'incidental-charges', 'Extra or miscellaneous charges added to a guest account.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (931, 3, 'Incidentals', 'incidentals', 'Charges incurred by participants of a tour, but are not included in the tour price.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (932, 3, 'Inclusive Tour', 'inclusive-tour', 'Tour in which all specific elements – transportation, airfare, hotels, transfers, and other costs – are offered for a flat rate. An inclusive tour does not necessarily cover all costs such as personal items and telephone.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (933, 3, 'Income Audit', 'income-audit', 'An accounting function, to verify and check on any inaccuracies that may occur in various Outlet transactions. (Items billed at correct quantity and price)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (934, 3, 'Income Statement', 'income-statement', 'A summary of a company\'s revenue, costs and expenses during a financial period. Also called &quot;profit and loss statement,&quot; &quot;income and expense statement,&quot; and &quot;operating statement.&quot;', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (935, 3, 'Income', 'income', 'Revenue less expenses. The type of income depends on the type of expenses deducted. Revenue less cost of goods is &quot;gross&quot; income, for example.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (936, 3, 'Independent Contractor', 'independent-contractor', 'A person contractually retained by another to perform certain specific tasks The other person has no control over the independent contractor other than as provided in the contract. In the context of group travel, a tour manager is often retained by the tour operator, or tour brochure designer/writer might be hired in this capacity.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (937, 3, 'Independent Hotel', 'independent-hotel', 'A hotel that is not associated with a Franchise.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (938, 3, 'Independent Tour', 'independent-tour', 'An unescorted tour sold through agents to individuals. For one price, the client guaranteed air travel, hotel room, attraction admissions and (typically) a car rental.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (939, 3, 'Indexes', 'indexes', 'Reference points a travel agent uses to locate a hotel near where their client is going. These can be companies, attraction, etc.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (940, 3, 'Indigenous Species', 'indigenous-species', 'A species that occurs at a place within its historically known natural range, and forms part of the natural biological diversity of a place.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (941, 3, 'Indirect Air Carrier', 'indirect-air-carrier', 'Generally synonymous with charter tour operator. A tour operator, travel agent, or other promoter who (under federal regulations) contracts for charter space from a carrier for resale to the public. In theory, indirect air carriers act as independent, risk-taking entrepreneurs, promoting their own product.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (942, 3, 'Individual Deposit', 'individual-deposit', 'Funds advanced to a hotel for the purpose of guaranteeing a reservation.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (943, 3, 'info-mediaries', 'info-mediaries', 'Organisations which provide websites/electronic guides as an information resource, sharing other resources such as web links to organisations that sell tourism/travel. The infomediary may be an organisation or company in its own right, or may form part of an individual company\'s or organization\'s customer service', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (944, 3, 'Information Systems', 'information-systems', 'Systems that use information technology to capture, transmit, store, retrieve, manipulate, or display information.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (945, 3, 'Infrastructure', 'infrastructure', 'Construction needed to support economic development.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (946, 3, 'In-house Laundry', 'in-house-laundry', 'A hotel-operated department that launders linens, uniforms, bedspreads etc.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (947, 3, 'Ink-jet', 'ink-jet', 'A printer that produces small dots printed with liquid ink on paper.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (948, 3, 'Innovative best Practice', 'innovative-best-practice', 'A practice that is considered to be of the highest quality, excellence, or standing – a leader in the field.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (949, 3, 'Inquiries / Reports', 'inquiries-reports', 'A feature of the PMS that enables management to maintain a current view of operations and finances.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (950, 3, 'In-room Guest Checkout', 'in-room-guest-checkout', 'A feature of the property management system that allows the guest to use a guest room television to check out of a hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (951, 3, 'In-service Education', 'in-service-education', 'Courses that update a professional’s educational background for use in current practice.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (952, 3, 'Intangibility', 'intangibility', 'The characteristic of not being touchable : a good is tangible whereas a service is intangible', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (953, 3, 'Inter-departmental Communication', 'inter-departmental-communication', 'Communication between departments.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (954, 3, 'Interfacing', 'interfacing', 'The ability of computers to communicate electronically and share data.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (955, 3, 'Inter-hotel property Referrals', 'inter-hotel-property-referrals', 'A system in which one member-property recommends another member property to a guest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (956, 3, 'Interhotel Property Referrals', 'interhotel-property-referrals', 'A system in which one member-property recommends an-other member-property to a guest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (957, 3, 'Intermediary', 'intermediary', 'An organisation within the chain of distribution whose function is to facilitate the supply of a given product from producers to consumers. In the travel industry examples are travel agencies and tourism information offices.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (958, 3, 'Intermediate Carrier', 'intermediate-carrier', 'A carrier that transports a passenger or piece of baggage as part of an inter-line movement, but on which neither the point of origin or destination is located.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (959, 3, 'Intermodal Tour', 'intermodal-tour', 'Tour using several forms of transportation such as airplanes, motorcoaches, cruise ships, and trains to create a diversified and efficient tour package.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (960, 3, 'International Association of Travel Agents', 'international-association-of-travel-agents', 'Well-know and reputable organization of travel agencies.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (961, 3, 'Internet', 'internet', 'A network of computer systems that share information over high-speed electronic connections.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (962, 3, 'Interpretation', 'interpretation', 'An educational process that is intended to stimulate and facilitate people\'s understanding of place, so that empathy towards, conservation, heritage, culture and landscape is developed.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (963, 3, 'Interpretation', 'interpretation', 'Revealing the significance and meanings of natural and cultural phenomena to visitors, usually with the intent of providing a satisfying learning experience and encouraging more sustainable behavior.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (964, 3, 'Intersell Cards', 'intersell-cards', 'Credit cards issued by a hotel corporation, similar to private label cards.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (965, 3, 'Introduced Species', 'introduced-species', 'A translocated or alien species found at a place outside its historically known natural range, as a result of the intentional or accidental dispersal by human activities. Includes genetically modified organisms.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (966, 3, 'Inventory', 'inventory', 'Stock or merchandise, operating supplies, and other items held for future use in a hotel. For example - linen, cleaning supplies and so on, are important housekeeping inventory items(Verb) To take “stock” / physical count of items.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (967, 3, 'Invoice', 'invoice', 'An itemized bill presented by the seller of goods and/or services to the buyer for the purpose of collecting payment.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (968, 3, 'IT Number', 'it-number', 'A registration number that is assigned to a tour package.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (969, 3, 'Itinerary', 'itinerary', 'The travel schedule provided by a travel agent or tour operator for the client. A proposed or preliminary itinerary may be rather vague or very specific. A final itinerary spells out all details, including flight numbers, departure times, and similar data, as well as describing planned activities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (970, 3, 'Jacuzzis', 'jacuzzis', 'Whirlpool ; small pools in which alternate, under-water jets of warm water, bring about a therapeutic effect (like a massage).', 'Ja-koo-zi');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (971, 3, 'Job Analysis', 'job-analysis', 'A detailed listing of the tasks performed in a job, which provides the basis for a “Job Description”.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (972, 3, 'Job Analysis', 'job-analysis', 'A detailed listing of the tasks performed in a job, which provides the basis for a sound job description.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (973, 3, 'Job Description', 'job-description', 'A list of required duties to be performed by an employee in a particular job.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (974, 3, 'Key Clerk', 'key-clerk', 'A person who issues keys to registered guests and other hotel personnel and sorts incoming mail for registered guests and management staff.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (975, 3, 'Key Drawer', 'key-drawer', 'A drawer located underneath the counter of the front desk that holds room keys in slots in numerical order.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (976, 3, 'Key Fob', 'key-fob', 'A decorative and descriptive plastic or metal tag attached to a hard key.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (977, 3, 'Keyboard', 'keyboard', 'A standard or Dvorak-type typewriter-style keypad that allows the operator to enter or retrieve data.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (978, 3, 'Keypad', 'keypad', 'A numeric collection of typewriter keys and function keys that allows the operator to enter numbers or perform math functions in a computer.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (979, 3, 'King-size Bed', 'king-size-bed', 'The largest size bed available, with a dimension of 78 inches x 80 inches ( eastern king ) or 72 x 80 inches ( California king )', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (980, 3, 'Labor Charge', 'labor-charge', 'Labor charges are fixed dollar amounts (not based on a percentage.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (981, 3, 'Lanai', 'lanai', 'A balcony, a patio or both, overlooking a landscaped area, a scenic view, water or garden.', 'La-nai');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (982, 3, 'Land Operator', 'land-operator', 'A company that provides local services, see also ground/receptive operator', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (983, 3, 'Landscape Area', 'landscape-area', 'An area where trees, plants, turf, deck, walks, ponds etc, have been used to create a natural looking outdoor space that is functional and visually appealing.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (984, 3, 'Laser', 'laser', 'A printer that produces photo images on paper.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (985, 3, 'Last Room Availability', 'last-room-availability', 'A room rate that is available as long as there are rooms left to sell in their hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (986, 3, 'Late Arrival', 'late-arrival', 'A guest with a reservation who notifies the hotel that he/she expects to arrive after 6:00 p.m.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (987, 3, 'Late Charges (AD)', 'late-charges-ad', '(a) Guest charges that might not be included on the guest folio because of a delay in posting by other departments. (b)Applied to guest who is Checking out after scheduled “Check-out” time. **AD – (After Departure)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (988, 3, 'Late Charges', 'late-charges', 'Charges made by the guest and received at the front desk for posting after the guest has checked out.', '');
