INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1213, 3, 'Prepaid Expense', 'prepaid-expense', 'An expense paid before the entirety of its benefit is received.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1214, 3, 'Pre-Registration', 'pre-registration', 'Registering guests, prior to their arrival.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1215, 3, 'Pre-registration', 'pre-registration', 'Room assignment and the filling out of registration cards prior to the guest\'s arrival, often used for convention, meeting and tour guests.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1216, 3, 'Price elasticity of Demand', 'price-elasticity-of-demand', 'A measure of the variability that can be expected in sales when prices are changed. Unity elasticity would see equal increase in sales to in reaction to a decrease in price. Inelastic demand would not change when prices went down or up.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1217, 3, 'Pricing', 'pricing', 'Decision-making process of ascertaining what price to charge for a given tour, once total costs are known. Pricing involves determining the markup, studying the competition, and evaluating the tour value for the price to be charged; function performed by the operations manager.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1218, 3, 'Primary Market', 'primary-market', 'A country in which the US Travel &amp; Tourism Admin (USTTA) maintains an office.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1219, 3, 'Printer', 'printer', 'Computer hardware in dot-matrix, ink-jet, or laser models that produces hard copies of output data in letter quality or draft style in various print fonts, with printing speed being expressed in CPS (characters per second), number of characters per line, and pages per minute and paper insertion being tractor-fed, single-sheet, or continuous-form.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1220, 3, 'Prior Approved Credit', 'prior-approved-credit', 'Use of a credit card to establish creditworthiness.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1221, 3, 'Private Label Cards', 'private-label-cards', 'Credit cards issued by a retail organization, such as a department store or gasoline company.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1222, 3, 'Process Control', 'process-control', 'A systematic use of tools to identify significant variations in operational performance and output quality, determine root causes, make corrections and verify results.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1223, 3, 'Process Design', 'process-design', 'Involves specifying all practices needed, flowcharting, rationalization and error prevention.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1224, 3, 'Process Improvement', 'process-improvement', 'A proactive task of management aimed at continual monitoring of a process and its outcome and developing ways to enhance its future performance.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1225, 3, 'Process Management', 'process-management', 'Planning and administering the activities necessary to achieve a high level of performance in a process and identifying opportunities for improving quality, operational performance and ultimately customer satisfaction. It involves design, control and improvement of key business processes.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1226, 3, 'Process', 'process', 'A set of interrelated or interacting activities which transforms inputs into outputs.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1227, 3, 'Processor Speed', 'processor-speed', 'How fast a CPU (central processing unit) makes calculations per second; expressed in MHz (the abbreviation for “megahertz”)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1228, 3, 'Productivity Standards', 'productivity-standards', 'The quantity of work expected to be completed by each department employee.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1229, 3, 'Profit', 'profit', 'The difference between the amount of revenue and expenses, if positive.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1230, 3, 'Profit', 'profit', 'The excess of revenue over expenses, if expenses exceed revenues in a given period the organization will make a loss.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1231, 3, 'Profit-and-loss Statement', 'profit-and-loss-statement', 'A listing of revenues and expenses for a certain time period.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1232, 3, 'Proof of Citizenship', 'proof-of-citizenship', 'A document, necessary for obtaining a passport, that establishes one s nationality.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1233, 3, 'Protected Area', 'protected-area', 'Any area of land and/or sea dedicated to the conservation, protection and maintenance of biodiversity and natural and cultural resources, which is managed through legal or other means.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1234, 3, 'Protected', 'protected', 'Guarantee by a supplier or wholesaler to pay commissions, plus all refunds to clients, on pre-paid, confirmed bookings regardless of subsequent cancellation of a tour or cruise.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1235, 3, 'Pseudo city Code', 'pseudo-city-code', 'An ID code which identifies the location of the terminal of a travel agency which makes reservation for a designated company. This allows classified information (negotiated rates for a given company) to be accessible only to that agency. This is also referred to as &quot; defined viewership.&quot;', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1236, 3, 'Psychographic Data', 'psychographic-data', 'Emotional and motivational forces that affect a service or product for potential markets.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1237, 3, 'Public Policy', 'public-policy', 'Is whatever governments choose to do or not to do (Thomas Dye 1992: 2). Such a definition covers government action, inaction, decisions and non-decisions as it implies a very deliberate choice between alternatives.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1238, 3, 'Public Space', 'public-space', 'The areas of the hotel generally accessible by all hotel guests, such as the lobby, corridors and pool deck.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1239, 3, 'Purchase Order', 'purchase-order', 'A contract authorizing the purchase of goods or services.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1240, 3, 'Quality', 'quality', 'The degree to which a set of inherent characteristics of a product fulfils customer requirements.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1241, 3, 'Qualmark', 'qualmark', 'Classification and grading system for the New Zealand tourism industry, using 5 star system.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1242, 3, 'Rack Rate', 'rack-rate', 'The highest room rate category offered by a hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1243, 3, 'Rack Rate', 'rack-rate', 'Retail price of accommodation, airfares, activities / attractions.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1244, 3, 'Rate Tier', 'rate-tier', 'Different levels of rates that are placed in Special Rate Plans and are categorized by moderate, high, and heavy room-night volume. These levels of rates may be structured as a percentage off base rate, dollars off base rate, or a flat room rate. Typically used for locally negotiated rates established in exchange for volume.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1245, 3, 'Ratings', 'ratings', '“Star “ Ratings commonly used for classifying Hotels, 5-star being highest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1246, 3, 'Real Estate Investment Trust (REIT)', 'real-estate-investment-trust-reit', 'A form of financing an investment in real estate through a mutual fund.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1247, 3, 'Recreation Director', 'recreation-director', 'The person who is in charge of developing and organization recreational activities for guests.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1248, 3, 'Referral Member', 'referral-member', 'A hotel owner or developer who has access to the national reservation system.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1249, 3, 'Referral Property', 'referral-property', 'A hotel operating as an independent that wishes to be associated with a certain chain; uses national reservation system.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1250, 3, 'Referral Reservation Service', 'referral-reservation-service', 'A service offered by a management company of a chain of hotels to franchisee members.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1251, 3, 'Registration Card', 'registration-card', 'A form on which the guest indicates name, home or billing address, phone number, date of departure, method of payment etc.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1252, 3, 'Registration Card', 'registration-card', 'Card used by guests to sign in at check-in.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1253, 3, 'Regret', 'regret', 'A potential reservation not booked. Common regret reasons are: rate, room type not available and the location of the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1254, 3, 'Regulation', 'regulation', 'Control through formalized processes.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1255, 3, 'Renewable Energy', 'renewable-energy', 'Energy sources that are practically inexhaustible. For example solar, hydro and wind energy.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1256, 3, 'Requirements', 'requirements', 'Stated, generally implied (as a custom or common practice for the organization, its customers and other interested parties) or obligatory needs.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1257, 3, 'Reseller\'s Permit', 'reseller-s-permit', 'Effectively, a sales tax license, allowing the holder to collect sales tax and avoid paying sales tax for goods purchased for resale to consumers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1258, 3, 'Reservation Code', 'reservation-code', 'A sequential series of alphanumeric combinations that provide the guest with a reference for a guaranteed reservation.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1259, 3, 'Reservation Confirmation', 'reservation-confirmation', 'A written notice sent to a reservation maker or traveler that a reservation has been made.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1260, 3, 'Reservation Deposit', 'reservation-deposit', 'Funds advanced to a hotel for the purpose of guaranteeing a reservation or group of reservations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1261, 3, 'Reservation Referral System', 'reservation-referral-system', 'A worldwide organization that processes requests for room reservations at a particular member-hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1262, 3, 'Reservation Status', 'reservation-status', 'Terminology used to indicate the availability of a guest room to be rented on a particular night, i.e., open (room is available for renting), confirmed (room has been reserved until 4:00 p.m. or 6:00 p.m.) guaranteed (room has been reserved until guest arrives), and repair (room is not available for guest rental)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1263, 3, 'Reservation', 'reservation', 'The holding of a guest-room for occupancy.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1264, 3, 'Reservations Manager', 'reservations-manager', 'The person who takes and confirms incoming requests for rooms, noting special requests for service; provides guest with requested information; maintains an accurate room inventory; and communicates with marketing and sales.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1265, 3, 'Reserve', 'reserve', 'Assignment of a vacant room on the day of arrival to a reservation in an effort to accommodate a special request.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1266, 3, 'Resident Manager', 'resident-manager', 'A hotel or motel manager who lives at the work site.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1267, 3, 'Residential Hotel', 'residential-hotel', 'Hotels that provide long term accommodations for guest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1268, 3, 'Resort Hotel', 'resort-hotel', 'A fashionable hotel, usually located in a resort area , with other attractions. Some hotels are built specifically to create a captive trade e.g. the casinos and holiday resorts of Macau, Hainan and the Caribbean.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1269, 3, 'Resort Hotel', 'resort-hotel', 'A fashionable hotel, usually located in a resort area , with other attractions. Some hotels are built specifically to create a captive trade e.g. the casinos and holiday resorts of Macau, Hainan and the Caribbean.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1270, 3, 'Resort', 'resort', 'A hotel, motel or condominium complex located in an area associated with recreation and leisure, such as the mountains or the seashore. Normally offer facilities for sports and recreational activities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1271, 3, 'Responsibility Clause', 'responsibility-clause', 'That section of a brochure that spells out the conditions under which a tour is sold. The clause should name the party responsible for the tour financially.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1272, 3, 'Responsible Tourism', 'responsible-tourism', 'Type of tourism which is practised by tourists who make responsible choices when choosing their holidays. These choices reflect reponsible attitudes to the limiting of the extent of the sociological and environmental impacts their holiday may cause.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1273, 3, 'Restoration', 'restoration', 'Returning existing habitats to a known past state, or to an approximation of the natural condition, through repairing degradation, removing introduced species, and revegetating using native locally occurring species.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1274, 3, 'Restricted Date', 'restricted-date', 'A date for which management has limited the type of reservation to be accepted.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1275, 3, 'Returned Cheques', 'returned-cheques', 'Dishonored cheques returned by the bank to the depositor.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1276, 3, 'Revenue Account', 'revenue-account', 'Part of owner’s equity.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1277, 3, 'Revenue Departments', 'revenue-departments', 'Departments that generate revenue.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1278, 3, 'Revenue expenditure', 'revenue-expenditure', 'The cost of resources consumed or used up in the process of generating revenue, generally referred to as expenses.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1279, 3, 'Revenue Management', 'revenue-management', 'A process of planning to achieve maximum room rate and most profitable.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1280, 3, 'Revenue Management', 'revenue-management', 'Revenue management is a management approach to optimising revenue, often based on managing revenues around capacity and timing (yield management), for different market segments or from different sources of funding.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1281, 3, 'Revenue per Available Room (RevPAR)', 'revenue-per-available-room-revpar', 'The amount of dollars each hotel room produces for the overall financial success of the hotel, determined by dividing room revenues re-ceived for a specific day by the number of rooms available in the hotel for that day.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1282, 3, 'Revenue Realized', 'revenue-realized', 'The actual amount of room revenue earned (number of rooms sold actual rate).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1283, 3, 'Revenue', 'revenue', 'The cash value of a company\'s receipts for the sale of goods and/or services. (Revenue does not include taxes collected, but &quot;gross receipts&quot; does.)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1284, 3, 'RevPAR', 'revpar', 'Revenue Per Available Room. The amount of money a hotel earns for each available room. (Occupancy x ADR).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1285, 3, 'RFP', 'rfp', 'Request for proposal. The opportunity to offer a company or consortium a discounted rate(s).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1286, 3, 'RM', 'rm', 'Resident Manager.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1287, 3, 'Role-playing', 'role-playing', 'Acting out a role before actually being required to do the job.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1288, 3, 'Rollaway', 'rollaway', 'A portable bed for one person.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1289, 3, 'Room Attendants', 'room-attendants', 'Employees who clean and maintain guest rooms and public areas.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1290, 3, 'Room Blocking', 'room-blocking', 'Reserving rooms for guests who are holding reservations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1291, 3, 'Room Change', 'room-change', 'Moving of a guest from one room to another during his or her stay.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1292, 3, 'Room Key Control System', 'room-key-control-system', 'An administrative procedure that authorizes certain personnel and registered guests to have access to keys', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1293, 3, 'Room Rates', 'room-rates', 'Day rate: usually one-half the regular rate for a room during the day up to 5 pm; flat rate: a specific room rate for a group agreed upon by the hotel/group in advance; group rate: rate based on an agreed upon minimum number of rooms used, also called flat rate; net group rate: a wholesale rate for group business (usually a minimum of 10 and 15 people) to which an operator may add a markup if desired; net wholesale rate: a rate usually lower than the group rate, applicable to groups or individuals when a hotel is specifically mentioned in a tour folder; published rate: a full rate available to or advertised to the public, The rate can change, depending upon the season. Also known as rack rate.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1294, 3, 'Room Revenue', 'room-revenue', 'The amount of room sales received.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1295, 3, 'Room Revenues', 'room-revenues', 'The amount of room sales received.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1296, 3, 'Room Sales Figure', 'room-sales-figure', 'The total of posted daily guest room charges.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1297, 3, 'Room Sales Projections', 'room-sales-projections', 'A weekly report prepared and distributed by the front office man-ager that indicates the number of departures, arrivals, walk-ins, stayovers, and no-shows.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1298, 3, 'Room Service', 'room-service', 'Food or beverages served in a guest’s room.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1299, 3, 'Room Status Discrepancy', 'room-status-discrepancy', 'A situation in which the housekeeping departments description of a rooms status differs (is different) from the room status information with the front office.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1300, 3, 'Room Status Report', 'room-status-report', 'Allows the housekeeping department to identify the occupancy or condition of the property‟s rooms. It is made daily through a two-way communication, between Housekeeping and Front office.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1301, 3, 'Room Status', 'room-status', 'Information on availability of entry to a guest room—reservation (open, confirmed, guaranteed, or repair) or housekeeping (ready, on change, or out-of-order).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1302, 3, 'Rooming List', 'rooming-list', 'Given in advance of a group arrival, which lists the names of those who will be staying in the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1303, 3, 'Rooming', 'rooming', 'Group room arrival sheet. A list of names informing us of the individual guests who will occupy a specific block of rooms held for a group. Sometimes a list is used in lieu of telephone or mailed reservations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1304, 3, 'Rooms Forecast', 'rooms-forecast', 'The projection of room sales for a specific period.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1305, 3, 'Rule-of-Thumb Method for determining Room Rates', 'rule-of-thumb-method-for-determining-room-rates', 'Guideline stipulating that the room rate should be $1 for every $1,000 of construction costs (this figure is from the 1960s; the current figure is $2 for every $1,000 of construction costs)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1306, 3, 'Safety Committee', 'safety-committee', 'A group of frontline employees and supervisors who discuss safety issues concerning guests and employees.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1307, 3, 'Sales Associate', 'sales-associate', 'A person who books the guest’s requirements for banquets and other special events.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1308, 3, 'Sales Indicators', 'sales-indicators', 'Number of guests and revenue generated.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1309, 3, 'Sales Tax', 'sales-tax', 'Tax collected by merchants on behalf of state and local governments when selling some goods and services.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1310, 3, 'Sales', 'sales', 'Revenue from ordinary activities: not necessarily cash.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1311, 3, 'Sauna', 'sauna', 'Small room or hut, heated to approximately 80degrees C. It is used for dry heat Cleansing as well as for mental &amp; physical relaxation.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1312, 3, 'Scanty Baggage', 'scanty-baggage', 'A room status indicating a room assigned to guest with small, light and few pieces of luggage that could be carried away without obviously indicating departure, should a guest walk out with them.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1313, 3, 'Seasonality', 'seasonality', 'A phenomenon created by either tourism supply or demand (or both) changing according to the time of the year.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1314, 3, 'Sector', 'sector', 'A part or branch of the whole industry that provides particular goods and/or services.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1315, 3, 'Security Deposit', 'security-deposit', 'A payment made by a hotel guest guaranteeing the payment of debts for services or damaged or missing hotel property.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1316, 3, 'Security', 'security', 'A hotel department or function provided by hotel employees or a hotel contractor to provide for the safety and protection of hotel guests, employees and property.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1317, 3, 'Self-check-in Process', 'self-check-in-process', 'A procedure that requires the guest to insert a credit card with a magnetic stripe containing personal and financial data into a self check in terminal and answer a few simple questions concerning the guest stay.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1318, 3, 'Sell Up', 'sell-up', 'Employment of selling techniques to persuade guests to choose a higher rate room then originally intended.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1319, 3, 'Sell-through', 'sell-through', 'The condition or act of taking a reservation that extends through a sold-out date. For example, a hotel may take a five-day reservation through a period in which one of the five days is sold-out to not lose the revenue on the other four days.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1320, 3, 'Series Operator', 'series-operator', 'A travel agent, wholesaler, tour operator, or broker who blocks space in advance for a series of movements over a given period of time, not necessarily on a back-to-back basis.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1321, 3, 'Service Animals', 'service-animals', 'Animals used to help people overcome physical disabilities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1322, 3, 'Service Charge', 'service-charge', '(1) a specified percentage of a hotel’s daily rate (usually 10% or 15 %) charged to the guest, who in return is relieved of the responsibility for tipping; (2) a fee charged to a client by a travel agent in addition to the commissions paid to him or her by the principals.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1323, 3, 'Service Charge', 'service-charge', 'Amounts collected, mostly on behalf of service employees, for banquet services, usually calculated as a fixed percentage of the revenue subject to the service charge. Also called banquet &quot;tips&quot; and &quot;gratuities,&quot;', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1324, 3, 'Service Delivery', 'service-delivery', 'The manner in which customer needs are met.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1325, 3, 'Service Directory', 'service-directory', 'This is a booklet in which the services offered to guests by the hotel are listed, along with the intercom numbers to reach the relevant departments.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1326, 3, 'Service Encounter', 'service-encounter', 'The moments of interface between customer and supplier.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1327, 3, 'Service Management Program', 'service-management-program', 'A management program that highlights a company’s focus on meeting customers’ needs and allows a hotel to achieve its financial goals.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1328, 3, 'Service Marketing Mix', 'service-marketing-mix', 'The addition of People, Physical Evidence and Process to the four areas of activity more usually associated with marketing products,: Price, Place, Promotion and Product.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1329, 3, 'Service Provider', 'service-provider', 'A person or company that supplies a particular service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1330, 3, 'Service Strategy Statement', 'service-strategy-statement', 'A formal recognition by management that the hotel will strive to deliver the products and services desired by the guest in a professional manner.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1331, 3, 'Service', 'service', 'Non-physical, intangible attributes that management controls, including friendliness, efficiency, attitude, professionalism, and responsiveness.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1332, 3, 'Service', 'service', 'Work done for the benefit of another.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1333, 3, 'Servicescape', 'servicescape', 'The location in which the service encounter takes place.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1334, 3, 'Shelving', 'shelving', 'The capability of allowing a travel agency to book a rate other than the base rate through an airline system.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1335, 3, 'Shift Leader', 'shift-leader', 'The person responsible for directing the efforts of a particular work shift.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1336, 3, 'Shore Excursion', 'shore-excursion', 'A land tour, usually available at ports of call and sold by cruise lines or tour operators to cruise passengers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1337, 3, 'Short Haul', 'short-haul', '1-3 hour flight.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1338, 3, 'Shoulder Season', 'shoulder-season', 'Period when there is neither a high nor low demand for a destination, usually falling in the spring or fall months for most areas.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1339, 3, 'Show Rooms', 'show-rooms', 'Rooms set aside for showing to prospective clients and customers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1340, 3, 'Single Supplement', 'single-supplement', 'An extra charge assessed to a tour purchased for single accommodations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1341, 3, 'Single', 'single', 'A room rented to one guest only and charged at the single rate.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1342, 3, 'Single-sheet', 'single-sheet', 'A type of printer that uses single-sheet paper.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1343, 3, 'Site destination selection Company', 'site-destination-selection-company', 'Company that investigates and suggests potential meeting sites to suit corporate or association needs.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1344, 3, 'Skill Demonstration', 'skill-demonstration', 'Demonstration of specific tasks required to complete a job.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1345, 3, 'Skills Gaps', 'skills-gaps', 'Employers perceive existing employees have lower skill levels than needed to achieve business objectives, or where new, apparently trained and qualified for specific occupations, entrants still lack requisite skills.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1346, 3, 'Skills Shortages', 'skills-shortages', 'Lack of adequately skilled individuals in the labour market due to low unemployment, sufficiently skilled people in the labour market but not easily geographically accessible or insufficient appropriately-skilled individuals.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1347, 3, 'Skipper', 'skipper', 'A room status that indicates the guest has left the hotel without making arrangements to settle his/her account.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1348, 3, 'Sleep Out', 'sleep-out', 'A guest who is registered into a room, but who informs the front desk that he or she will be sleeping in the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1349, 3, 'Sleeper', 'sleeper', 'A room that is thought to be occupied but is in fact vacant.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1350, 3, 'Small Business', 'small-business', 'A small business is one which has a small number of employees, profit and/or revenue. Often these are owner-managed, with few specialist managers. Some definitions of small businesses distinguish between businesses with under 10 employees, which are micro-businesses, and those with 10-49 employees, which are classified as small businesses.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1351, 3, 'SME(s)', 'sme-s', 'Small and Medium Enterprises.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1352, 3, 'Smart Card', 'smart-card', 'An electronic device with a computer chip that allows a guest or an employee access to a designated area, tracking, and debit-card capabilities for the hotel guest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1353, 3, 'Social', 'social', 'Relating to human society and interaction between its members.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1354, 3, 'Software', 'software', 'Computer-designed applications that process data such as guest information and aid in financial transactions and report generation.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1355, 3, 'Sold out', 'sold-out', 'Status of the hotel when all rooms have been rented and there are no further rooms available. When the hotel reaches sold-out status, no more reservations or walk ins may be accepted for that date.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1356, 3, 'Souvenir', 'souvenir', 'A product purchased by a tourist as a reminder of a holiday.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1357, 3, 'Special Event Tour', 'special-event-tour', 'A tour designed around a particular event, e.g. : Mardi Gras', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1358, 3, 'Special Interest Tour', 'special-interest-tour', 'A tour designed to appeal to clients with a curiosity or concern about a specific subject. Most special interest tours provide an expert tour leader and usually visit places and/or events only relevant to that interest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1359, 3, 'Special Market', 'special-market', 'A foreign country with high potential for US inbound travel which does not have an office of the US Travel and Tourism Administration (USTTA).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1360, 3, 'Spillage', 'spillage', 'High valued customers turned away because lower valued customers have been previously accepted.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1361, 3, 'Split Shift', 'split-shift', 'A work schedule interrupted by non-paid time, except meal and rest breaks.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1362, 3, 'Split Transaction', 'split-transaction', 'A single entry in computer accounting programs that has more than one offsetting account.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1363, 3, 'Stakeholder', 'stakeholder', 'Any person, group or organization with an interest in, or who may be affected by, the activities of another organization.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1364, 3, 'State travel office', 'state-travel-office', 'An official government agency or privately run, non-profit organization responsible for travel development and promotion of a state or province.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1365, 3, 'State', 'state', '\'The state\' is a set of officials with their own preferences and capacities to effect public policy, or in more structural terms a relatively permanent set of political institutions operating in relation to civil society\' (Nordlinger 1981, in Hall and Jenkins 1995). The state includes elected politicians, interest or pressure groups, law enforcement agencies, the bureaucracy, and a plethora of rules, regulations, laws, conventions and policies.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1366, 3, 'Statement', 'statement', '1. A summary of a customer\'s account, including debit and credit entries for charges, payments and credits totaling to a balance due. 2. Any of a number of financial reports.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1367, 3, 'Statute', 'statute', 'The law as made by parliament, e.g. in the UK, the Disability Discrimination Act (1995). A statute is made up of many parts called \'sections\' or \'provisions\'.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1368, 3, 'Statutory Instrument', 'statutory-instrument', 'The vast majority of delegated legislation in the UK is in the form of statutory instruments governed by the Statutory Instruments Act 1946.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1369, 3, 'Stay-over', 'stay-over', 'A registered guest who extend his or her stay beyond the check-out date originally requested.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1370, 3, 'Stayovers', 'stayovers', 'Currently registered guests, who wish to extend their stay beyond the time for which they made reservations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1371, 3, 'Step-on Guide', 'step-on-guide', 'An independent guide who comes aboard a motor-coach to give an informed overview of the city or attraction to be toured.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1372, 3, 'Stock taking (Inventory)', 'stock-taking-inventory', 'The physical verifying of items by counting up stocks, at periodic intervals. Stock taking is also termed conducting inventory.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1373, 3, 'Strategic Information Systems', 'strategic-information-systems', 'Systems designed to support the strategic management decision processes and implementation.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1374, 3, 'Strategy Pyramid', 'strategy-pyramid', 'A visual way of representing the different levels of the strategy conceptualisation and implementation process. The most general assumptions are shown at the apex and the practical, implementation actions are at the base.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1375, 3, 'Subcontractor', 'subcontractor', 'A local operator who provides services for a wholesaler.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1376, 3, 'Suite', 'suite', 'A sitting room ( parlor, living room, salon ) connected to at least one sleeping room.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1377, 3, 'Supplier', 'supplier', 'The actual producer of a unit of travel merchandise or service such as a hotel or restaurant.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1378, 3, 'Suppliers', 'suppliers', 'Individuals, companies or other organizations which provide goods or services to a recognizable customer or consumer.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1379, 3, 'Surcharge Rates', 'surcharge-rates', 'Telephone rates for adding service charges for out-of-state long-distance telephone service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1380, 3, 'Surety Bond', 'surety-bond', 'Insurance to guarantee that an insure will carry out the specific work he or she was hired to do.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1381, 3, 'Surveillance Equipment', 'surveillance-equipment', 'Equipment such as CCTV’s ( Close circuit televisions ) that help to closely observe suspicious activities and persons.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1382, 3, 'Suspense Account', 'suspense-account', 'An account used to temporarily carry amounts (such as discrepancies) pending their analysis.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1383, 3, 'Sustainable Development', 'sustainable-development', 'Development carried out in such a way as to meet the needs of the present without compromising the ability of future generations to meet their needs.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1384, 3, 'Sustainable Tourism', 'sustainable-tourism', 'According to the World Tourism Organization, this is &quot;envisaged as leading to management of all resources in such a way that economic, social and aesthetic needs can be fulfilled with maintaining cultural integrity, essential ecological processes, biological diversity, and life support systems.&quot;', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1385, 3, 'Sustainable Tourism', 'sustainable-tourism', 'Tourism that can be sustained over the long term because it results in a net benefit for the social, economic, natural and cultural environments of the area in which it takes place.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1386, 3, 'Sustainable', 'sustainable', 'Something which can be kept in the same or a better condition for the future.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1387, 3, 'Tariff', 'tariff', 'Published Rate of Rooms.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1388, 3, 'Tariff', 'tariff', '(1) fare or rate from a supplier; (2) class or type of a fare or rate; (3) published list of fares or rates from a supplier; (4) official publication compiling rates or fares and conditions of service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1389, 3, 'Tax Cumulative Total Feature', 'tax-cumulative-total-feature', 'An electronic feature of a PMS that adds all posted room tax amounts previously entered into one grand total.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1390, 3, 'Telecheck', 'telecheck', 'A company that electronically approves customers\' checks.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1391, 3, 'Telephone Initiation and Reception Agreements', 'telephone-initiation-and-reception-agreements', 'Contracts between senders and receivers of PictureTel concerning specifications of the telephone call and who pays for the call.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1392, 3, 'Telephone Operator', 'telephone-operator', 'The person who handles incoming and outgoing calls, locates registered guests and management staff, deals with emergency communication, and assists the desk clerk and cashier when necessary.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1393, 3, 'Tent Cards', 'tent-cards', 'Hotel publicity cards in the shape of tents placed in guestrooms, or Dining Rooms.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1394, 3, 'Themed Tour', 'themed-tour', 'A tour designed around a specific theme such as fall foliage, also a special interest tour.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1395, 3, 'Tickler Files', 'tickler-files', 'Files used to prompt notice that certain events will be occurring.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1396, 3, 'Timeshares', 'timeshares', 'Vacation “interval” hotels. These involve individuals purchasing the ownership of accommodations for a specific period of time, usually one or two weeks a year. These “owners” then can occupy the unit during that time. Owner may also have the unit rented out, by the management company that operates the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1397, 3, 'TIPS', 'tips', 'Extra money (gratuity) given by the guest, as a reward for quality service, received. T.I.P.S – (the acronym), means “To Insure Prompt/Polite Service”. Tipping is now the accepted “norm”, as many facilities will add an automatic 10-20% Gratuity (Service charge), which usually supplements the salary of personnel, who earn minimum wage.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1398, 3, 'Top Down', 'top-down', 'A sales method that involves presenting the most expensive rate first.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1399, 3, 'TOT', 'tot', 'Transient Occupancy Tax', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1400, 3, 'Total Quality Management', 'total-quality-management', 'TQM : a management technique that encourages managers to look at processes used to produce products and services with a critical eye.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1401, 3, 'Total Restaurant Sales Figure', 'total-restaurant-sales-figure', 'Total of all sales incurred at restaurants or food outlets in the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1402, 3, 'Touch Screen', 'touch-screen', 'A type of computer monitor screen that allows the operator to input data by touch.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1403, 3, 'Tour Basing Fare', 'tour-basing-fare', 'A reduced-rate excursion fare available only to those who buy pre-paid tours or packages. Tour basing fares include inclusive tours, group inclusive tours, incentive tours, contract bulk inclusive tours, and group round-trip inclusive tours.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1404, 3, 'Tour Booker', 'tour-booker', 'A person or company who holds an ICC license to organize and market motor-coach tours.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1405, 3, 'Tour Broker', 'tour-broker', 'A person or company which organizes and markets tours.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1406, 3, 'Tour Catalog', 'tour-catalog', 'A publication by tour wholesalers listing their tour offerings.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1407, 3, 'Tour Consultant', 'tour-consultant', 'Individual within an agency selling and advising clients regarding a tour. The consultant is sometimes a salesperson with particular expertise in escorted tour sales.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1408, 3, 'Tour Departure', 'tour-departure', 'The date of the start by any individual or group of a tour program, also used in referral to the entire operation of that single tour.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1409, 3, 'Tour Escort', 'tour-escort', 'The tour company staff member or independent contractor who conducts the tour. Often called the tour manager or tour director. It is technically a person that only escorts the group and does not have charge of the commentary portion.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1410, 3, 'Tour Manager', 'tour-manager', 'A person employed as the escort for a group of tourists, usually for the duration of the entire trip, perhaps supplemented by local guides.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1411, 3, 'Tour Manual', 'tour-manual', '(1) a summary of facts about a company s rules, regulations, and official procedures; (2) a compendium of facts about a destination, including its attractions, accommodations, geography, and special events, used by destination marketing organizations to attract tour operators and visitors and their area.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1412, 3, 'Tour Menu', 'tour-menu', 'A menu that limits group clients to two or three choices at a special price.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1413, 3, 'Tour Operator', 'tour-operator', 'A person or company which creates and/or markets inclusive tours and subcontracts with suppliers to create a package. Most tour operators sell through travel agents and/or directly to clients.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1414, 3, 'Tour Option', 'tour-option', 'Any component of a package tour that is not included in the package price, but may be purchased as an added feature to extend the length of the package or enhance the trip.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1415, 3, 'Tour Order', 'tour-order', 'A coupon given to the purchaser of a tour package, identifying the tour, the seller, and the fact that the tour is pre-paid. It is used as a form of proof of payment and receives vouchers for meals, porterage, transfers, entrance fees, and other expenses. Also see tour vouchers.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1416, 3, 'Tour Organizer', 'tour-organizer', 'Person who locates and creates groups for preformed tours. The tour organizer is often compensated only with a free trip.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1417, 3, 'Tour Vouchers', 'tour-vouchers', 'Documents issued by tour operators to be exchanged for tour components, also called coupons.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1418, 3, 'Tour', 'tour', 'Any pre-arranged journey to one or more destinations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1419, 3, 'Tourism Commodity', 'tourism-commodity', 'Tourism Commodity is one for which a significant part of its total demand in Canada comes from visitors.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1420, 3, 'Tourism Demand', 'tourism-demand', 'Tourism Demand is defined as the spending of Canadian and non-resident visitors on domestically produced commodities. It is the sum of tourism domestic demand and tourism exports.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1421, 3, 'Tourism Domestic Demand', 'tourism-domestic-demand', 'Tourism Domestic Demand is the spending in Canada by Canadian visitors on domestically produced commodities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1422, 3, 'Tourism Employment', 'tourism-employment', 'Tourism Employment is a measure of employment in tourism and non-tourism industries. It is based on an estimate of jobs rather than “hours of work”. Thus, someone who works 10 hours a week counts for as much, by this measure, as someone who works 50 hours a week.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1423, 3, 'Tourism Flows', 'tourism-flows', 'The major movements of tourists from specific home areas to destinations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1424, 3, 'Tourism Geography', 'tourism-geography', 'The knowledge of countries, regions, major cities, gateways, famous icons, monuments, building structures, and geographical features such as rivers, seas, mountains, deserts and time zones.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1425, 3, 'Tourism Income Multiplier (TIM)', 'tourism-income-multiplier-tim', 'Exaggerated effect of a change in tourism expenditure on an area\'s income.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1426, 3, 'Tourism Industry', 'tourism-industry', 'A group of businesses that provide services and facilities for consumption by tourists.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1427, 3, 'Tourism Industry', 'tourism-industry', 'Tourism Industry is an industry that would cease to exist or would continue to exist only at significantly reduced levels of activity in the absence of tourism.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1428, 3, 'Tourism Infrastructure', 'tourism-infrastructure', 'Roads, railway lines, harbors, airport runways, water, electricity, other power supplies, sewerage disposal systems and other utilities to serve not only the local residents but also the tourist influx (suitable accommodation, restaurants and passenger transport terminals form the superstructure of the region)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1429, 3, 'Tourism Product', 'tourism-product', 'Different things to the various members of the tourism industry. To the hotel it is `guest- nights\'. To the airline it is the `seats flown\' and the `passenger miles\'. To the museum, art gallery or archaeological site, the product is measured in terms of the number of visitors. For the tourist the product is the complete experience resulting from the package tour or travel facility purchased, from the time they leave home until their return.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1430, 3, 'Tourism Satellite Account', 'tourism-satellite-account', 'System of accounting at national or regional level which reveals the total direct impact of tourism on the economy.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1431, 3, 'Tourism System', 'tourism-system', 'A framework that identifies tourism as being made up of a number of components, often taken to include the tourist, the tourist generating region, the transit route region, the tourist destination and the tourism industry.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1432, 3, 'Tourism', 'tourism', 'The all-embracing term for the movement of people to destinations away from their place of residence for any reason other than following an occupation, remunerated from within the country visited, for a period of 24 hours or more.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1433, 3, 'Tourism', 'tourism', 'The business of providing and marketing services and facilities for leisure travelers. Thus, the concept of tourism is of direct concern to governments, carriers, and the lodging, restaurant, and entertainment industries, and of indirect concern to virtually every industry and business in the world.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1434, 3, 'Tourism', 'tourism', 'The definition of tourism used in the national tourism indicators (NTI) is that adopted by the World Tourism Organization and the United Nations Statistical Commission: “the activities of persons travelling to and staying in places outside their usual environment for not more than one consecutive year for leisure, business and other purposes.”', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1435, 3, 'Tourist Attractions', 'tourist-attractions', 'Tourist attractions are defined as being destinations for visitors\' excursions which are routinely accessible to visitors during opening hours. Visitors can include local residents, day-trippers or people who are travelling for business or leisure purposes. Formal definitions exclude shops, sports stadia, theatres and cinemas, as these meet a wider purpose, although in practice tourists may consider the excluded categories to be tourist attractions.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1436, 3, 'Tourist Card', 'tourist-card', 'A kind of visa issued to tourists prior to entering a country (required in addition to a passport or other proof of citizenship).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1437, 3, 'Tourist Facility', 'tourist-facility', 'A feature created for utilization by tourists.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1438, 3, 'Tourist Route', 'tourist-route', 'A route developed to attract tourists to an area to view or experience something unique to that area (e.g. wine route, whale route, heritage route, battlefield route)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1439, 3, 'Tourist Trend', 'tourist-trend', 'A general tendency to visit a country, region or destination or to pursue a specific tourist activity.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1440, 3, 'Tourist', 'tourist', 'Anyone who spends at least one night away from home, no matter what the purpose.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1441, 3, 'Tourist', 'tourist', 'One who travels for a period of 24 hours or more in a place other than that in which he or she usually resides, whose purpose could be classified as leisure (whether for recreation, health, sport, holiday, study or religion), business, family, mission or meeting.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1442, 3, 'TOWS Matrix', 'tows-matrix', 'Uses a SWOT analysis to develop strategies by matching strengths with opportunities, using opportunities to reduce weaknesses, using strengths to overcome threats, and reducing weaknesses and avoiding threats.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1443, 3, 'Tracking Research', 'tracking-research', 'Ongoing research conducted at regular intervals to track changes in specific factors, for example, potential customers\' intention to travel to NZ.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1444, 3, 'Tracking', 'tracking', 'A cause of action or method of monitoring, such as tracking the number of tours that come into a specific destination.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1445, 3, 'Tractor-fed', 'tractor-fed', 'A type of printer that uses a continuous roll of paper.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1446, 3, 'Traffic Managers', 'traffic-managers', 'Persons who direct hotel guests to available elevators in the lobby.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1447, 3, 'Training Tickler File', 'training-tickler-file', 'A database that keeps track of training sessions and alerts trainers to important upcoming dates.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1448, 3, 'Transfer Slip', 'transfer-slip', 'A form used to transfer an amount of money from one account to another while creating a paper trail.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1449, 3, 'Transfer', 'transfer', 'Local transportation, sometimes including porterage, as from one carrier terminal to another, from terminal to a hotel, or from a hotel to an attraction.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1450, 3, 'Transient Guest', 'transient-guest', 'An individual (non-group) hotel customer.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1451, 3, 'Transient occupancy tax (T.O.T.) (also bed tax, occupancy tax, room tax)', 'transient-occupancy-tax-t-o-t-also-bed-tax-occupancy-tax-room-tax', 'Tax on the sale of lodging rooms.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1452, 3, 'Transient Revenue', 'transient-revenue', 'Room revenue from services provided to an individual (non-group) customer.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1453, 3, 'Transient', 'transient', 'An individual not directly associated with a particular group or convention.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1454, 3, 'Transit Visa', 'transit-visa', 'Visa allowing the holder to stop over in a country to make a travel connection or brief visit.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1455, 3, 'Transit', 'transit', 'Process of changing planes without going through security and/or customs.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1456, 3, 'Travel Agent', 'travel-agent', 'A Travel Agentis a person or business that sells travel-related services to customers on behalf of suppliers such as airlines, cruise lines, hotels etc.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1457, 3, 'Travel Directories', 'travel-directories', 'Organized listings of hotel reservation methods and specific accommodations information.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1458, 3, 'Travel Directories', 'travel-directories', 'Organized listings of hotel reservation access methods and hotel geo-graphic and specific accommodations information.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1459, 3, 'Travel-agent Commission', 'travel-agent-commission', 'Percentage of room rate paid to travel agent for sending a guest to our hotel. Payment is usually 10%.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1460, 3, 'Traveler’s Checks', 'traveler-s-checks', 'Prepaid checks that have been issued by a bank or other financial organization.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1461, 3, 'Traveler\'s Checks ', 'traveler-s-checks', 'Prepaid checks that have been issued by a bank or other financial organization.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1462, 3, 'Trial Balance', 'trial-balance', 'A first run on a set of debits to determine their accuracy against a corresponding set of credits.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1463, 3, 'Trial Balance', 'trial-balance', 'The process of checking that the total of all debit entries equals the total of all credit entries for a period.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1464, 3, 'Trip Director', 'trip-director', 'An escort for an incentive company. Larger companies reserve this title for the person who directs all personnel and activities for a trip.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1465, 3, 'True Integration', 'true-integration', 'The sharing of a reservation database by a hotel’s central reservation system and property management system.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1466, 3, 'Turn Down Service', 'turn-down-service', 'A special service provided by the housekeeping department in which a room attendant enters the guestroom early in the evening to re- stock supplies , tidy the room and turn down the covers on the bed, in preparation for the night.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1467, 3, 'Understays', 'understays', 'Guests who arrive on time but decide to leave before their predicted date of departure.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1468, 3, 'Up-grade Voucher', 'up-grade-voucher', 'Guest receives change of status –room etc., to a higher rate/grade.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1469, 3, 'Upgrade', 'upgrade', 'To move to a better accommodation or class of service.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1470, 3, 'Upgrade', 'upgrade', 'When a guest is given a higher priced room for no additional charge.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1471, 3, 'Upsell', 'upsell', 'To encourage a customer to consider buying a higher priced product or service than originally anticipated.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1472, 3, 'Utell', 'utell', 'A large, worldwide reservation service renamed to Rezsolutions.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1473, 3, 'V &amp; R', 'v-amp-r', 'Vacant and ready, a guest room that is ready to be occupied.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1474, 3, 'V.I.P.', 'v-i-p', 'Abbreviation for &quot;Very Important Person&quot;.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1475, 3, 'Vacant', 'vacant', 'The status of a room in which no guest has slept the previous night and is not yet occupied.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1476, 3, 'Valet Service', 'valet-service', 'Laundry, Dry cleaning, shoe cleaning service provided to Hotel Guests.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1477, 3, 'Valet', 'valet', '(a) A man employed for personal services like pressing and shoe cleaning, for hotel or cruise-ship patrons. (b) An attendant who parks cars for patrons of a Hotel Restaurant etc. (This Service is usually contracted out)', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1478, 3, 'Value Season', 'value-season', 'A time of year when prices are lower than peak, also called low or off-season.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1479, 3, 'Van Log', 'van-log', 'A record of the use of a company vehicle.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1480, 3, 'Variability', 'variability', 'Because the production and the consumption of a tourism experience are inseparable and because differing circumstances and people will affect each experience, those experiences are prone to variance and create a challenge for tourism managers to achieve consistency of standards.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1481, 3, 'Variable Cost', 'variable-cost', 'An expense that changes in direct proportion to a related increase or decrease in total activity or volume.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1482, 3, 'VAT/TVA/MWS/GST', 'vat-tva-mws-gst', 'Acronyms for value-added tax, a tax system which adds a fixed percentage of taxation on products and services at each step of production or service delivery. Common in Europe and Canada.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1483, 3, 'Virtual Organization', 'virtual-organization', 'Organization in which major processes are outsourced to partners.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1484, 3, 'Visa Waiver', 'visa-waiver', 'A program to eliminate the visa requirement for selected countries.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1485, 3, 'Visa', 'visa', 'Stamp of approval recorded in a passport to enter a country for a specific purpose.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1486, 3, 'Visitors', 'visitors', 'A broader category than \'tourist\', includes tourists and same-day visitors.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1487, 3, 'Visitors', 'visitors', 'Visitors are persons who undertake tourism as defined above. They are referred to as either tourists (those who stay overnight or longer in the place visited), or same-day visitors.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1488, 3, 'Visual Alarm Systems', 'visual-alarm-systems', 'Flashing lights that indicate a fire or other emergency in a hotel room.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1489, 3, 'Waiting List', 'waiting-list', 'A list of prospective customers awaiting availability of room reservations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1490, 3, 'Waitlist', 'waitlist', 'List of clients awaiting transportation or accommodations at times when they are not available, confirmed as a result of subsequent cancellations.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1491, 3, 'Wake-up Call', 'wake-up-call', 'Telephone “Morning Call” Service, provided at guest\'s request.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1492, 3, 'Walk in Guest', 'walk-in-guest', 'Guest who requests a room rental, without having made a reservation.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1493, 3, 'Walk', 'walk', 'Lodging-industry slang for relocating a guest to another hotel because reserved accommodations are not available.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1494, 3, 'Walk-In', 'walk-in', 'Person without prior reservation who requests accommodation for that night.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1495, 3, 'Walking a Guest with a Reservation', 'walking-a-guest-with-a-reservation', 'Offering accommodations at another hotel to a guest who has a reservation when your hotel is overbooked', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1496, 3, 'Water Closet (WC)', 'water-closet-wc', 'Sanitary fittings consisting of the toilet bowl, tank etc.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1497, 3, 'Wholesaler', 'wholesaler', 'A company that usually creates and markets inclusive tours and FITs for sale through travel agents. Although the term is used often as a synonym for tour operator there are several distinctions: (1) presumably sells nothing at retail while a tour operator often does both; (2) does not always create his/her own products, while a tour operator always does; (3) is less inclined than a tour operator to perform local services.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1498, 3, 'Wholesaler', 'wholesaler', 'An agency which packages tours. A package may consist of a combination of transportation, hotel accommodation and/or attractions.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1499, 3, 'Wi-fi', 'wi-fi', 'Wireless fidelity. This is an amenity provided nowadays by world class hotels. Wi- fi‟ enables', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1500, 3, 'Working Capital', 'working-capital', 'Operational assets and liabilities needed for everyday operation, e.g. cash or bank overdraft, stock and trade creditors, known as net current assets/liabilities.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1501, 3, 'Working Supervisor', 'working-supervisor', 'A person who participates in the actual work performed while supervising.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1502, 3, 'World Heritage Area', 'world-heritage-area', 'Land of cultural and/or natural significance inscribed on the World Heritage List.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1503, 3, 'World Heritage Site', 'world-heritage-site', 'A site designated by UNESCO as being of special historical, cultural or natural importance.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1504, 3, 'Yield Management', 'yield-management', '&quot;A revenue maximization technique which aims to increase net yield through the predicted allocation of available … capacity to predetermined market segments at optimal price&quot; (Donaghy et al., 1997a).', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1505, 3, 'Yield Management', 'yield-management', 'A process of planning to achieve maximum room rates and most profitable guests (guests who will spend money at the hotel’s food and beverage outlets, gift shops, etc.), which encourages front office managers, general managers, and market-ing and sales directors to target sales periods and develop sales programs that will maximize profit for the hotel.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1506, 3, 'Yield Percentage', 'yield-percentage', 'The effectiveness of a hotel at selling its rooms at the highest rate avail-able to the most profitable guest.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1507, 3, 'Yield', 'yield', 'The percentage of income that could be secured if 100 percent of available rooms are sold at their full rack rate.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1508, 3, 'Zip drive', 'zip-drive', 'A computer accessory that holds data; a 100-megabyte Zip drive holds an equivalent of 70 floppy disks', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1509, 3, 'Zip or Postal Code', 'zip-or-postal-code', 'A postal designation assigned by country, to facilitate the efficient movement of mail.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1510, 3, 'Zip or Postal Code', 'zip-or-postal-code', 'An individual local postal designation assigned by a country.', '');
INSERT INTO `dictionary` (`id`, `lang_key`, `phrase`, `slug`, `meaning`, `pronounce`) VALUES (1511, 3, 'Zoning', 'zoning', 'Different eco-systems may be zoned in terms of their robustness to pressures from tourism in an attempt to mitigate environmental damage.', '');
